async def add_basic_persons(engine: AsyncEngine) -> None:
    try:
        # –ü—ã—Ç–∞–µ–º—Å—è –¥–æ–±–∞–≤–∏—Ç—å —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ –∑–Ω–∞—á–µ–Ω–∏—è
        async with engine.begin() as conn:
            for person in personality_base:
                await conn.execute(BasicPersonality.__table__.insert(), person)
        logger.success("–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –ª–∏—á–Ω–æ—Å—Ç–µ–π –¥–æ–±–∞–≤–ª–µ–Ω—ã")
    except Exception:
        logger.info("–°—Ç–∞–Ω–¥–∞—Ä—Ç–Ω—ã–µ –Ω–∞—Å—Ç—Ä–æ–π–∫–∏ –ª–∏—á–Ω–æ—Å—Ç–µ–π —É–∂–µ –¥–æ–±–∞–≤–ª–µ–Ω—ã")
